mapper.insert:
insert into sys_group (id, groupname, viewname,
deleted, cruser,
crtime, mduser, mdtime
)
values (#{id,jdbcType=INTEGER}, #{groupname,jdbcType=VARCHAR}, #{viewname,jdbcType=VARCHAR},
#{deleted,jdbcType=BIT}, #{cruser,jdbcType=VARCHAR},
#{crtime,jdbcType=TIMESTAMP}, #{mduser,jdbcType=VARCHAR}, #{mdtime,jdbcType=TIMESTAMP}
)

mapper.select:
select id, groupname, viewname, deleted, cruser, crtime, mduser, mdtime
from sys_group
where deleted = 0 and id = #{id,jdbcType=INTEGER}
