mapper.update:
update sample
set
is_deleted = 1,
mduser = #{mduser,jdbcType=VARCHAR},
mdtime = #{mdtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

sql.where:
is_deleted = 0

    and bcode = #{bcode}



    and btitle LIKE #{btitle}


    and is_btinyint = #{btinyint}

mapper.select:
select

from sample
where is_deleted = 0 and id = #{id,jdbcType=BIGINT}

select
from sample
ORDER BY id DESC limit ${offset},${rows}
